import React, { useState, useEffect } from 'react';
import { Container, Navbar, Nav, Button, Modal, Form, Row, Col, Card } from 'react-bootstrap';
import './styles/App.css';

function App() {
  const [activeView, setActiveView] = useState('dashboard');
  const [allExpenses, setAllExpenses] = useState([]);

  // Collect all expenses from all folders
  const updateAllExpenses = (folders) => {
    const expenses = folders.reduce((acc, folder) => {
      return [...acc, ...folder.expenses.map(exp => ({ ...exp, folderName: folder.name }))];
    }, []);
    setAllExpenses(expenses);
  };

  return (
    <div className="App">
      <Navbar bg="dark" variant="dark" expand="lg" className="mb-4">
        <Container>
          <Navbar.Brand href="#home">Monthly Expense Tracker</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link active={activeView === 'dashboard'} onClick={() => setActiveView('dashboard')}>
                Dashboard
              </Nav.Link>
              <Nav.Link active={activeView === 'folders'} onClick={() => setActiveView('folders')}>
                Folders
              </Nav.Link>
              <Nav.Link active={activeView === 'summary'} onClick={() => setActiveView('summary')}>
                Summary
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Container className="main-content">
        {activeView === 'dashboard' && <Dashboard expenses={allExpenses} />}
        {activeView === 'folders' && <Folders onUpdateExpenses={updateAllExpenses} />}
        {activeView === 'summary' && <Summary expenses={allExpenses} />}
      </Container>

      <footer className="footer mt-auto py-3 bg-light">
        <Container className="text-center">
          <span className="text-muted">Made by Bugu for Bugi</span>
        </Container>
      </footer>
    </div>
  );
}

function Dashboard({ expenses }) {
  const calculateTotalExpenses = () => {
    return expenses.reduce((total, exp) => total + exp.amount, 0).toFixed(2);
  };

  const getRecentExpenses = () => {
    return [...expenses]
      .sort((a, b) => new Date(b.date) - new Date(a.date))
      .slice(0, 5);
  };

  const getCategoryTotals = () => {
    return expenses.reduce((acc, exp) => {
      acc[exp.category] = (acc[exp.category] || 0) + exp.amount;
      return acc;
    }, {});
  };

  return (
    <div className="dashboard">
      <h2>Welcome to Monthly Expense Tracker</h2>
      <div className="dashboard-image">
        <img 
          src="https://img.freepik.com/free-vector/expenses-concept-illustration_114360-5332.jpg" 
          alt="Expense Tracker"
          className="welcome-image"
        />
      </div>

      <Row className="mt-4">
        <Col md={4}>
          <Card className="dashboard-card">
            <Card.Body>
              <Card.Title>Total Expenses</Card.Title>
              <h3 className="text-primary">${calculateTotalExpenses()}</h3>
            </Card.Body>
          </Card>
        </Col>
        <Col md={8}>
          <Card className="dashboard-card">
            <Card.Body>
              <Card.Title>Recent Expenses</Card.Title>
              <div className="recent-expenses">
                {getRecentExpenses().map(expense => (
                  <div key={expense.id} className="recent-expense-item">
                    <span className="expense-title">{expense.title}</span>
                    <span className="expense-amount">${expense.amount}</span>
                    <span className="expense-category">{expense.category}</span>
                    <span className="expense-date">{new Date(expense.date).toLocaleDateString()}</span>
                  </div>
                ))}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

function ExpenseForm({ show, handleClose, folderId, onSave }) {
  const [expense, setExpense] = useState({
    title: '',
    amount: '',
    category: '',
    date: new Date().toISOString().split('T')[0],
    description: '',
    paymentMethod: '',
    tags: []
  });

  const categories = [
    'Rent',
    'Outside Food',
    'Grocery',
    'Family and Friends',
    'Travel',
    'Entertainment',
    'Utilities',
    'Shopping',
    'Healthcare',
    'Education',
    'Transportation',
    'Insurance',
    'Investments',
    'Personal Care',
    'Home Maintenance',
    'Gifts',
    'Subscriptions',
    'Other'
  ];

  const paymentMethods = [
    'Cash',
    'Credit Card',
    'Debit Card',
    'UPI',
    'Bank Transfer',
    'Digital Wallet'
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      ...expense,
      id: Date.now(),
      amount: parseFloat(expense.amount),
      timestamp: new Date().toISOString()
    });
    handleClose();
    setExpense({
      title: '',
      amount: '',
      category: '',
      date: new Date().toISOString().split('T')[0],
      description: '',
      paymentMethod: '',
      tags: []
    });
  };

  return (
    <Modal show={show} onHide={handleClose} size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Add New Expense</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Title</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter expense title"
                  value={expense.title}
                  onChange={(e) => setExpense({...expense, title: e.target.value})}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Amount</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Enter amount"
                  value={expense.amount}
                  onChange={(e) => setExpense({...expense, amount: e.target.value})}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Category</Form.Label>
                <Form.Select
                  value={expense.category}
                  onChange={(e) => setExpense({...expense, category: e.target.value})}
                  required
                >
                  <option value="">Select category</option>
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Payment Method</Form.Label>
                <Form.Select
                  value={expense.paymentMethod}
                  onChange={(e) => setExpense({...expense, paymentMethod: e.target.value})}
                  required
                >
                  <option value="">Select payment method</option>
                  {paymentMethods.map((method) => (
                    <option key={method} value={method}>{method}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Date</Form.Label>
                <Form.Control
                  type="date"
                  value={expense.date}
                  onChange={(e) => setExpense({...expense, date: e.target.value})}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Tags (comma-separated)</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="e.g., important, recurring"
                  value={expense.tags.join(', ')}
                  onChange={(e) => setExpense({...expense, tags: e.target.value.split(',').map(tag => tag.trim())})}
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Enter description"
              value={expense.description}
              onChange={(e) => setExpense({...expense, description: e.target.value})}
            />
          </Form.Group>

          <div className="d-flex justify-content-end">
            <Button variant="secondary" className="me-2" onClick={handleClose}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              Save Expense
            </Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

function Folders({ onUpdateExpenses }) {
  const [folders, setFolders] = useState([]);
  const [newFolderName, setNewFolderName] = useState('');
  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [selectedFolderId, setSelectedFolderId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');

  useEffect(() => {
    onUpdateExpenses(folders);
  }, [folders]);

  const handleAddFolder = () => {
    if (newFolderName.trim()) {
      setFolders([...folders, { id: Date.now(), name: newFolderName, expenses: [] }]);
      setNewFolderName('');
    }
  };

  const handleAddExpense = (folderId) => {
    setSelectedFolderId(folderId);
    setShowExpenseForm(true);
  };

  const handleSaveExpense = (expense) => {
    setFolders(folders.map(folder => {
      if (folder.id === selectedFolderId) {
        return {
          ...folder,
          expenses: [...folder.expenses, expense]
        };
      }
      return folder;
    }));
  };

  const filteredFolders = folders.map(folder => ({
    ...folder,
    expenses: folder.expenses.filter(expense => {
      const matchesSearch = expense.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          expense.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = !filterCategory || expense.category === filterCategory;
      return matchesSearch && matchesCategory;
    })
  }));

  return (
    <div className="folders">
      <h2>Your Folders</h2>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="New folder name"
          value={newFolderName}
          onChange={(e) => setNewFolderName(e.target.value)}
        />
        <Button variant="primary" className="mt-2" onClick={handleAddFolder}>
          Add Folder
        </Button>
      </div>

      <div className="filters mb-4">
        <Row>
          <Col md={6}>
            <Form.Control
              type="text"
              placeholder="Search expenses..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </Col>
          <Col md={6}>
            <Form.Select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              {categories.map((cat) => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </Form.Select>
          </Col>
        </Row>
      </div>

      <div className="folder-list">
        {filteredFolders.map((folder) => (
          <div key={folder.id} className="card mb-3">
            <div className="card-body">
              <div className="d-flex justify-content-between align-items-center">
                <h5 className="card-title mb-0">{folder.name}</h5>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  onClick={() => handleAddExpense(folder.id)}
                >
                  Add Expense
                </Button>
              </div>
              {folder.expenses.length > 0 && (
                <div className="mt-3">
                  <h6>Expenses:</h6>
                  <div className="expense-list">
                    {folder.expenses.map((expense) => (
                      <div key={expense.id} className="expense-item">
                        <div className="expense-header">
                          <strong>{expense.title}</strong>
                          <span className="expense-amount">${expense.amount}</span>
                        </div>
                        <div className="expense-details">
                          <span className="badge bg-primary">{expense.category}</span>
                          <span className="badge bg-secondary">{expense.paymentMethod}</span>
                          <span className="expense-date">{new Date(expense.date).toLocaleDateString()}</span>
                        </div>
                        {expense.tags.length > 0 && (
                          <div className="expense-tags">
                            {expense.tags.map((tag, index) => (
                              <span key={index} className="badge bg-info me-1">{tag}</span>
                            ))}
                          </div>
                        )}
                        {expense.description && (
                          <div className="expense-description">
                            {expense.description}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
      <ExpenseForm
        show={showExpenseForm}
        handleClose={() => setShowExpenseForm(false)}
        folderId={selectedFolderId}
        onSave={handleSaveExpense}
      />
    </div>
  );
}

function Summary({ expenses }) {
  // Implementation will be added in the next update
  return (
    <div className="summary">
      <h2>Expense Summary</h2>
      <div className="row">
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Monthly Summary</h5>
              {/* Chart will be added here */}
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Quarterly Summary</h5>
              {/* Chart will be added here */}
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Yearly Summary</h5>
              {/* Chart will be added here */}
            </div>
          </div>
        </div>
      </div>
    </div>
